<?phprequire_once '../config/DB.php';class M_Admin{    public function __construct($goodId, $goodName, $goodPrice, $goodDiscr){        $this->goodId = $goodId;        $this->goodName = $goodName;        $this->goodPrice = $goodPrice;        $this->goodDiscr = $goodDiscr;    }    public function delete_good(){        $sql = "DELETE FROM goods where id_good = :goodId";        $arg = ['goodId' => $this->goodId];        db::delete($sql, $arg);    }    public function save_good(){        $sql = "UPDATE `goods` SET `name`=:goodName, `price`=:goodPrice, `discription`=:goodDiscr WHERE `id_good`=:goodId";        $arg = ['goodName' => $this->goodName, 'goodPrice' => $this->goodPrice, 'goodDiscr' => $this->goodDiscr, 'goodId' => $this->goodId];        db::update($sql, $arg);    }}