<?phprequire_once '../config/DB.php';//require_once '../controllers/C_Catalog.php';//require_once '../controllers/C_Basket.php';//        $show = new C_Catalog();//        $show->showNext();if (isset($_GET['goods'])) {    $req = "SELECT * FROM `goods`";    $goods = db::getRows($req, []);    foreach ($goods as $val) {        echo            "<div class='galitem'>" .            "<img src=" . "catalogImg/" . $val['catalogImg'] . ">" .            "<h3>" . $val['name'] . "</h3>" .            "<p class='itemId'>" . $val['id_good'] . "</p>" .            "<p class='itemprice'>" . $val['price'] . "$" . "</p>" .            "<button>Редактировать</button>" .            "</div>";    }}//else echo "ID не передан";if (isset($_GET['orders'])) {    $req = "SELECT * FROM `orders`";    $orders = db::getRows($req, []);    foreach ($orders as $val) {        $users = db::getRow('SELECT * FROM user where id_user = :userId', ['userId' => $val['id_user']]);        echo            "<div class='admOrder'>" .            "<h4> Заказ №" . $val['id_order'] . "</h4>" .            "<p class=''>Дата: " . $val['datetime_create'] . "</p>" .            "<p class=''>Адрес доставки: " . $val['destination'] . "</p>" .            "<p class=''>ID пользователя: " . $val['id_user'] . "</p>" .            "<p class=''>Имя пользователя: " . $users['user_name'] . "</p>" .            "<p class=''>Сумма заказа: " . $val['amount'] . "$</p>" .            "<button id='edit_".$val['id_order']."'>"."Редактировать</button>" .            "<hr> </div>";    }}