<?phprequire_once '../config/DB.php';//require_once '../controllers/C_Catalog.php';//require_once '../controllers/C_Basket.php';//        $show = new C_Catalog();//        $show->showNext();$data = db::getRow('SELECT * FROM `goods` order by `id_good` LIMIT 1');$minId = $data['id_good'];// Узнаем наименьший Id в БДif (isset($_GET['next'])) {    if ($_GET['next'] > $minId) {        $req = "SELECT * FROM `goods` WHERE `id_good`>" . $_GET['next'] . " order by `id_good` LIMIT 4";        $goods = db::getRows($req, []);        foreach ($goods as $val){            echo                "<div class='galitem'>" .                "<img src=" . "catalogImg/" . $val['catalogImg']. ">" .                "<h3>" . $val['name'] . "</h3>" .                "<p class='itemId'>" . $val['id_good'] . "</p>" .                "<p class='itemprice'>" . $val['price'] . "$"."</p>" .                "</div>";        }    } return false;} else echo "ID не передан";